    private void buttonEqualsMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_buttonEqualsMouseClicked
        valor2 = Double.parseDouble(interfacecalc.getText());
        interfacecalc.setText("");
        if (sinal.equals("plus")){
            interfacecalc.setText(String.valueOf(valor1+valor2));
        } if (sinal.equals("minus")) {
            interfacecalc.setText(String.valueOf(valor1-valor2));
        } if (sinal.equals("times")) {
            interfacecalc.setText(String.valueOf(valor1*valor2));
        } if (sinal.equals("split")){
            interfacecalc.setText(String.valueOf(valor1/valor2));
        } if (sinal.equals("raised")) {
            double product = valor1;
            for (int i = 1; i < valor2; i++){
                product = product * valor1;
            }
            interfacecalc.setText(String.valueOf(product));
        }
    }//GEN-LAST:event_buttonEqualsMouseClicked

