    private void buttonPointMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_buttonPointMouseClicked
        if (!interfacecalc.getText().equals("")){
            if (!interfacecalc.getText().contains(".")){
                interfacecalc.setText(interfacecalc.getText()+".");
            }
        }   
    }//GEN-LAST:event_buttonPointMouseClicked

